//componentes
import axios from "axios";
import { useState } from "react";
import { useNavigate } from "react-router-dom";

const URI = "http://localhost:8000/blogs/";

const CompCreateuser = () => {
  const [nombre_aprendiz, setnombre_aprendiz] = useState("");
  const [apellido_aprendiz, setapellido_aprendiz] = useState("");
  const [celular_aprendiz, setcelular_aprendiz] = useState("");
  const [correo_aprendiz, setccorreo_aprendiz] = useState("");
  const navigate = useNavigate();

  //procedimiento guardar un usuario
  const store = async (e) => {
    e.preventDefault();
    await axios.post(URI, {
      nombre_aprendiz: nombre_aprendiz,
      apellido_aprendiz: apellido_aprendiz,
      celular_aprendiz: celular_aprendiz,
      correo_aprendiz: correo_aprendiz,
    });
    navigate("/");
  };

  return (
    <div>
      <h3>Create usuario</h3>
      <form onSubmit={store}>
        <div className="mb-3">
          <label className="form-label">Nombre</label>
          <input
            value={nombre_aprendiz} //aca no es titulo, como va a serlo, si los datos de la base de datos son otros
            onChange={(e) => setnombre(e.target.value)}
            type="text"
            className="form-control"
          />
        </div>
        <div className="mb-3">
          <label className="form-label">Apellido</label>
          <textarea
            value={apellido_aprendiz}
            onChange={(e) => setapellido(e.target.value)}
            type="text"
            className="form-control"
          />
        </div>
        <button type="submit" className="btn btn-primary">
          Guardar
        </button>
      </form>
    </div>
  );
};

export default CompCreateuser;
